    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection connect = DriverManager.getConnection("jdbc:mysql://localhost:3306/carmanagementsystem", "root", "123456");
            String qry = "SELECT * FROM sales";
            st = connect.createStatement();
            rs = st.executeQuery(qry);
            ResultSetMetaData rsmd = rs.getMetaData();
            DefaultTableModel model = (DefaultTableModel) jTable1.getModel();
            model.setRowCount(0);
            int cols = rsmd.getColumnCount()-1;
            String[] colName = new String[cols];
            for (int i=0;i<cols;i++){
                colName[i]=rsmd.getColumnName(i+1);
            }
            model.setColumnIdentifiers(colName);
            String cid,vid,eid,sNum,sDate,pType,sPrice;
            while (rs.next()){
                cid=rs.getString(1);
                vid=rs.getString(2);
                eid=rs.getString(3);
                sNum=rs.getString(4);
                sDate=rs.getString(5);
                pType=rs.getString(6);
                sPrice=rs.getString(7);
                String[] row={cid,vid,eid,sNum,sDate,pType,sPrice};
                model.addRow(row);
            }


        }catch (Exception e){
            System.out.println(e);

        }
    }//GEN-LAST:event_jButton2ActionPerformed

