    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection connect = DriverManager.getConnection("jdbc:mysql://localhost:3306/carmanagementsystem", "root", "123456");

            String qry = "INSERT sales SET Customer_ID=?,Vehicle_ID=?,Employee_ID=?,SaleNumber=?,SaleDate=?,PaymentType=?,SellingPrice=?";

            PreparedStatement pst = connect.prepareStatement(qry);
            pst.setString(1,jTextField6.getText());
            pst.setString(2,jTextField7.getText());
            pst.setString(3,jTextField8.getText());
            pst.setString(4,jTextField11.getText());
            pst.setString(5,jTextField12.getText());
            pst.setString(6,jTextField10.getText());
            pst.setString(7,jTextField9.getText());
            pst.execute();
            JOptionPane.showMessageDialog(this,"Executed Successfully");
            connect.close();
        }catch (Exception e){
            System.out.println(e);
            JOptionPane.showMessageDialog(this,e);

        }
    }//GEN-LAST:event_jButton3ActionPerformed

